function getQueryString(name) {
    var n = new RegExp("(^|&)" + name + "=([^&]*)(&|$)");
    var t = window.location.search.substr(1).match(n);
    return null != t ? unescape(t[2]) : null
}
$(function () {
    if ($('.page-body').data('current')) {
        $('.right-menu').find('.child-menu[data-nav="' + $('.page-body').data('current') + '"]').addClass('on');
    } else {
        $('.big-nav .right-menu .child-menu').eq(0).addClass('on');
    }
    $('.small-nav .right-menu .child-menu:last').mouseover(function () {
        $('.small-nav .right-menu .child-menu:last .ewm-box').css("display", "inline-block");
    });
    $('.small-nav .right-menu .child-menu:last').mouseout(function () {
        $('.small-nav .right-menu .child-menu:last .ewm-box').css("display", "none");
    });
    $(".left-fixed .close-a").click(function (e) {
        e.preventDefault(); $(this).parents(".left-fixed").hide();
    });
    $(".right-fixed .close-a").click(function (e) {
        e.preventDefault(); $(this).parents(".right-fixed").hide();
    });
    $(".popImgDialog .close-a").click(function (e) {
        e.preventDefault(); $(this).parents(".popImgDialog").hide();
    });
	 
});